class CreateDemo {
  static $text = '';
  static $pics = array ();

  public function __construct () {
  }

  private static function unicodeDecode ($name) {
    $pattern = '/([\w]+)|(\\\u([\w]{4}))/i';

    preg_match_all ($pattern, $name, $matches);
    
    if (!empty ($matches)) {
      $name = '';

      for ($j = 0; $j < count ($matches[0]); $j++) {
        $str = $matches[0][$j];
        
        if (strpos ($str, '\\u') === 0) {
          $code = base_convert (substr ($str, 2, 2), 16, 10);
          $code2 = base_convert (substr ($str, 4), 16, 10);
          $c = chr ($code) . chr ($code2);
          $c = iconv ('UCS-2', 'UTF-8', $c);
          $name.= $c;
        } else {
          $name.= $str;
        }
      }
    }
    return $name;
  }

  public static function eng ($length, $er = null) {
    if ($er == 'lower')
      $elements = array_merge (range ('a', 'z'));
    else if ($er == 'upper')
      $elements = array_merge (range ('A', 'Z'));
    else
      $elements = array_merge (range ('A', 'Z'), range ('a', 'z'));

    $elements = array_2d_to_1d (array_fill (0, ceil ($length / count ($elements)), $elements));
    shuffle ($elements);
    return implode ('', array_slice ($elements, 0, $length));
  }

  public static function num ($length) {
    $elements = range (0, 9);
    $elements = array_2d_to_1d (array_fill (0, ceil ($length / count ($elements)), $elements));
    shuffle ($elements);
    return implode ('', array_slice ($elements, 0, $length));
  }

  public static function url ($https = false) {
    $tmps = array_map (function () {
      return CreateDemo::eng (rand (2, 5), 'lower');
    }, array_fill (0, rand (2, 3), null));

    return ($https ? 'https://' : 'http://') . 'www.' . implode ('.', $tmps);
  }

  public static function engName ($length) {
    return CreateDemo::eng (1, 'upper') . CreateDemo::eng ($length - 1, 'lower');
  }

