  public static function email () {
    return CreateDemo::eng (rand (3, 5), 'lower') . '@' . implode ('.', array_map (function ($t) {
      return CreateDemo::eng (rand (3, 5), 'lower');
    }, array_fill (0, rand (2, 4), null)));
  }

  public static function pics ($length, $tags = array ('台灣之美')) {
    if (!count (self::$pics)) self::$pics = self::flickrPics ($tags);
    $pics = array_slice (self::$pics, 0, $length);
    self::$pics = array_slice (self::$pics, $length);
    return $length == 1 ? $pics[0] : $pics;
  }

  private static function flickrPics ($tags = array ('台灣之美')) {
    $url = 'https://api.flickr.com/services/rest/?';
    
    $params = array (
        'jsoncallback' => '?',
        'method' => 'flickr.photos.search',
        'api_key' => config ('demo', 'flickr_key'),
        'tags' => implode (',', $tags),
        'per_page' => '200',
        'extras' => 'url_m',
        'sort' => 'interestingness-desc',
        'format' => 'json',
        );

    $options = array (CURLOPT_URL => $url, CURLOPT_POST => false, CURLOPT_POSTFIELDS => http_build_query ($params), CURLOPT_TIMEOUT => 120, CURLOPT_HEADER => false, CURLOPT_MAXREDIRS => 10, CURLOPT_AUTOREFERER => true, CURLOPT_CONNECTTIMEOUT => 30, CURLOPT_RETURNTRANSFER => true, CURLOPT_FOLLOWLOCATION => true, CURLOPT_USERAGENT => "Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/32.0.1700.76 Safari/537.36");
    $ch = curl_init ($url);
    curl_setopt_array ($ch, $options);
    $data = curl_exec ($ch);
    curl_close ($ch);

    $data = str_replace ('jsonFlickrApi(', '', $data);
    $data = substr ($data, 0, strlen ($data) - 1);
    $object = json_decode ($data);

    return array_filter (array_map (function ($p) {
      return isset ($p->url_m) && $p->url_m && isset ($p->title) && $p->title ? array (
        'title' => $p->title,
        'url' => $p->url_m) : null;
    }, $object->photos->photo));
  }
